class WeatherData {  final int code;  final String temperature;  final String city;  final String wind;  final String sunrise;  final String sunset;  final String precipitation;  final String description;  WeatherData({    this.code,    this.temperature,    this.city,    this.wind,    this.sunrise,    this.sunset,    this.precipitation,    this.description  });  static WeatherData fromMap(Map data) {    return WeatherData(      code: int.parse(data['weather']['code']),      temperature: data['temp'].toString(),      city: data['city_name'],      wind: data['wind_spd'].toString(),      sunrise: data['sunrise'],      sunset: data['sunset'],      precipitation: data['precip'].toString(),      description: data['weather']['description'],    );  }}