import 'package:flutter/material.dart';class WeatherScreen extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(      home: Scaffold(        appBar: AppBar(          brightness: Brightness.light,          title: Text(            'SEPT 23',            style: TextStyle(              fontSize: 16,              color: Colors.black,            ),          ),          elevation: 0,          centerTitle: true,          leading: GestureDetector(            onTap: () {              print('menu');            },            child: Icon(              Icons.menu,              color: Colors.black,              size: 30,            ),          ),          actions: <Widget>[            Container(              width: 50,              child: GestureDetector(                onTap: () {                  print('add');                },                child: Icon(                  Icons.add,                  color: Colors.black,                  size: 30,                ),              ),            ),          ],          shape: Border(            bottom: BorderSide(              width: 1.5,              color: Colors.black.withOpacity(0.1),            ),          ),          backgroundColor: Colors.transparent,        ),        body: Column(          children: <Widget>[            Expanded(              flex: 5,              child: Container(                decoration: BoxDecoration(                  gradient: LinearGradient(                    begin: Alignment.bottomCenter,                    end: Alignment.topCenter,                    colors: [                      Color(0xFFF13370),                      Color(0xFFFBC389)                    ],                  ),                ),              ),            ),            Expanded(              flex: 3,              child: Container(                color: Colors.white,              ),            ),          ],        ),        extendBodyBehindAppBar: true,      ),    );  }}