import 'package:flutter/material.dart';class AppBarItem extends StatelessWidget {  AppBarItem({this.icon, this.padding, this.onTap});  final IconData icon;  final EdgeInsetsGeometry padding;  final Function onTap;  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: onTap,      child: Padding(        padding: padding,        child: Icon(icon, color: Colors.black, size: 30),      ),    );  }}