import 'package:flutter/material.dart';import 'package:weather_app/screens/weather_screen/widgets/detail_item.dart';class DetailGridView extends StatelessWidget {  DetailGridView({@required this.children});  final List<DetailItem> children;  @override  Widget build(BuildContext context) {    return GridView.builder(      shrinkWrap: true,      physics: NeverScrollableScrollPhysics(),      itemCount: children.length,      padding: EdgeInsets.symmetric(vertical: 15),      gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(        crossAxisCount: 2,        crossAxisSpacing: 100,        mainAxisSpacing: 20,        childAspectRatio: 2.35      ),      itemBuilder: (context, index) => children[index],    );  }}