import 'package:flutter/material.dart';import 'package:weather_app/enums/weather_detail-type.dart';class DetailItem extends StatelessWidget {  DetailItem({    @required this.type,    @required this.title,    @required this.image,    @required this.details,    this.color,  });  final WeatherDetailType type;  final String title;  final Image image;  final String details;  final Color color;  @override  Widget build(BuildContext context) {    return Column(      mainAxisSize: MainAxisSize.min,      crossAxisAlignment: CrossAxisAlignment.start,      children: <Widget>[        Padding(          padding: EdgeInsets.only(bottom: 5),          child: Text(            title,            style: TextStyle(              color: color,              fontWeight: FontWeight.bold,              fontFamily: 'Aftika',            ),          ),        ),        Row(          children: <Widget>[            image,            SizedBox(width: 5),            Text(              details,              style: TextStyle(                color: color,                fontFamily: 'Aftika',              ),            )          ],        ),      ],    );  }}