import 'package:flutter/material.dart';class GradientView extends StatelessWidget {  GradientView({this.colors, this.child});  final List<Color> colors;  final Widget child;  @override  Widget build(BuildContext context) {    return Container(      child: child,      width: double.infinity,      decoration: BoxDecoration(        gradient: LinearGradient(          begin: Alignment.bottomCenter,          end: Alignment.topCenter,          colors: colors ?? Colors.white,        ),      ),    );  }}