import 'package:flutter/material.dart';import 'package:weather_app/constants.dart';import 'package:weather_app/screens/weather_screen/widgets/app_bar_item.dart';class WeatherAppBar extends AppBar {  final Function leftItemTap;  final Function rightItemTap;  WeatherAppBar({    @required String title,    this.leftItemTap,    this.rightItemTap,  }) : super(          title: Text(title, style: kAftikaBlack16),          elevation: 0,          centerTitle: true,          leading: AppBarItem(            icon: Icons.menu,            padding: EdgeInsets.only(left: 20),            onTap: leftItemTap,          ),          actions: [            AppBarItem(              icon: Icons.add,              padding: EdgeInsets.only(right: 20),              onTap: rightItemTap,            )          ],          shape: Border(              bottom:                  BorderSide(width: 1.5, color: Colors.black.withOpacity(0.1))),          brightness: Brightness.light,          backgroundColor: Colors.transparent,        );}